// Componente para mostrar noticias individuales
function NoticiaDetalle() {
  const { id } = useParams();
  const [noticia, setNoticia] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulamos la carga de una noticia específica
    const fetchNoticia = () => {
      try {
        const noticiaEncontrada = noticiasPeru.find(n => n.id === id);
        setTimeout(() => {
          setNoticia(noticiaEncontrada || null);
          setLoading(false);
        }, 500);
      } catch (error) {
        console.error("Error:", error);
        setLoading(false);
      }
    };

    fetchNoticia();
  }, [id]);

  if (loading) return <div className="cargando">Cargando noticia...</div>;
  if (!noticia) return <div className="error">Noticia no encontrada</div>;

  return (
    <div className="noticia-detalle">
      <h2>{noticia.title}</h2>
      <p className="noticia-fecha">{noticia.date}</p>
      <img 
        src={noticia.imageUrl} 
        alt={noticia.title} 
        className="noticia-imagen"
        onError={(e) => {
          e.target.onerror = null; 
          e.target.src = 'https://via.placeholder.com/800x400?text=Imagen+No+Disponible';
        }}
      />
      <p className="noticia-contenido">{noticia.content}</p>
      <a 
        href={noticia.sourceUrl} 
        target="_blank" 
        rel="noopener noreferrer" 
        className="fuente-original"
      >
        Ver noticia completa en fuente original
      </a>
      <Link to="/actualidad" className="volver">← Volver a noticias</Link>
    </div>
  );
}

// Componente principal Actualidad
function Actualidad() {
  const [noticias, setNoticias] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Simulamos la carga de noticias
    const fetchNoticias = () => {
      try {
        setTimeout(() => {
          setNoticias(noticiasPeru);
          setLoading(false);
        }, 800);
      } catch (error) {
        console.error("Error fetching news:", error);
        setError("Error al cargar las noticias");
        setLoading(false);
      }
    };

    fetchNoticias();
  }, []);

  if (loading) return <div className="cargando">Cargando noticias...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="actualidad-container">
      <h1>Últimas Noticias del Perú</h1>
      
      <div className="subcontenido-actualidad">
        <Routes>
          <Route path="noticia/:id" element={<NoticiaDetalle />} />
          <Route index element={
            <div className="noticias-actualidad">
              <h2>Noticias Recientes</h2>
              
              <div className="lista-noticias">
                {noticias.map((noticia) => (
                  <div className="noticia-item" key={noticia.id}>
                    <Link to={`noticia/${noticia.id}`}>
                      <h3>{noticia.title}</h3>
                    </Link>
                    <p className="noticia-fecha">{noticia.date}</p>
                    <div className="imagen-container">
                      <img 
                        src={noticia.imageUrl} 
                        alt={noticia.title} 
                        className="noticia-imagen-lista"
                        onError={(e) => {
                          e.target.onerror = null; 
                          e.target.src = 'https://via.placeholder.com/300x200?text=Imagen+No+Disponible';
                        }}
                      />
                    </div>
                    <p className="noticia-resumen">{noticia.summary}</p>
                    <a 
                      href={noticia.sourceUrl} 
                      target="_blank" 
                      rel="noopener noreferrer" 
                      className="fuente-lista"
                    >
                     
                    </a>
                  </div>
                ))}
              </div>
            </div>
          } />
        </Routes>
      </div>
    </div>
  );
}

export default Actualidad; 